//import { render } from 'node-sass';
import { useState, useEffect } from 'react';
//import styles from '../styles/index.module.scss'

export default function home(props) {
  const [date, setDate] = useState(new Date());
  const [hour, setHour] = useState('')
  const [minute, setMinute] = useState('')
  const second = 1;
  var [events, setEvents] = useState([])
  var [contador, setContador] = useState(0)
 //Replaces componentDidMount and componentWillUnmount
 useEffect(() => {
  var timerID = setInterval( () => tick(), 1000 );
  var timer2 = setInterval( () => check(), 1000 ); 

  return function cleanup() {
      clearInterval(timerID);
      clearInterval(timer2);
    };
 });

   function tick() {
    setDate(new Date());
   }


   function check(){
     if (hour == date.getHours() && minute == date.getMinutes()
     && second == date.getSeconds()){
       alert('hello');

     }
   }

   function agendarHorario(hours, minutes){
    const evento = {
    name: `evento ${contador}`,
    hora : hours,
    minuto : minutes};
    setEvents = events.push(evento);

    }

    const myArrCreatedFromMap = events.map((item, i) => (<li key={item + i}>{item.name}</li>));
    const myList =() => {
      return(<ul>{myArrCreatedFromMap}</ul>) // `myArrCreatedFromMap` will evaluate to a list of `<li>` elements
    }


   return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {date.toLocaleTimeString()}.</h2>
        <h1>Your time is: {hour}:{minute}</h1>
        <h1>{myList}</h1>


        <input onChange={event => setHour(event.target.value)} />
        <input onChange={event => setMinute(event.target.value)} />
        <button onClick={agendarHorario(hour, minute)}>aaa</button>
      </div>
    );
}
